<script>
document.addEventListener("DOMContentLoaded", function() {
    const table = document.querySelector("table");
    const checkboxes = table.querySelectorAll("input[type='checkbox']");
    const memoCells = table.querySelectorAll("td:nth-child(5)");

    // Load saved state
    checkboxes.forEach((cb, i) => {
        const checked = localStorage.getItem("checkbox_" + i);
        if (checked === "true") {
            cb.checked = true;
            cb.closest("tr").classList.remove("pending");
            cb.closest("tr").classList.add("done");
        }
        cb.addEventListener("change", () => {
            localStorage.setItem("checkbox_" + i, cb.checked);
            cb.closest("tr").classList.toggle("done", cb.checked);
            cb.closest("tr").classList.toggle("pending", !cb.checked);
        });
    });

    // Make memo editable and save
    memoCells.forEach((cell, i) => {
        cell.contentEditable = true;
        const savedText = localStorage.getItem("memo_" + i);
        if (savedText) {
            cell.innerText = savedText;
        }
        cell.addEventListener("input", () => {
            localStorage.setItem("memo_" + i, cell.innerText);
        });
    });
});
</script>
