from __future__ import annotations
import hashlib, json, os, time, random, pathlib
from typing import Any, Iterable, List, Dict

# --- UUIDv7 (spec-aligned minimal) ---
def uuid7() -> str:
    ts_ms = int(time.time() * 1000)
    if ts_ms >= (1 << 48):
        raise ValueError("timestamp out of range for UUIDv7")
    rnd_a = random.getrandbits(12)
    rnd_b = random.getrandbits(62)
    time_hi = ts_ms & ((1<<48)-1)
    a = time_hi << 80
    a |= (0x7 << 76)              # version 7
    a |= (rnd_a & 0xFFF) << 64    # 12 random bits
    b = rnd_b & ((1<<62)-1)
    b |= (0b10 << 62)             # RFC 4122 variant
    val = a | b
    hex128 = f"{val:032x}"
    return f"{hex128[0:8]}-{hex128[8:12]}-{hex128[12:16]}-{hex128[16:20]}-{hex128[20:32]}"

# --- JCS minimal: sorted keys, no spaces, utf-8 ---
def jcs_dumps(obj: Any) -> bytes:
    return json.dumps(obj, ensure_ascii=False, separators=(",", ":"), sort_keys=True).encode("utf-8")

def sha256_hex(data: bytes) -> str:
    return hashlib.sha256(data).hexdigest()

# Append-only JSONL event log with hash chaining
def append_event_log(log_path: str, event: Dict[str, Any]) -> Dict[str, Any]:
    path = pathlib.Path(log_path)
    path.parent.mkdir(parents=True, exist_ok=True)
    prev_hash = None
    if path.exists() and path.stat().st_size > 0:
        with path.open("rb") as f:
            try:
                f.seek(-2048, os.SEEK_END)
            except OSError:
                f.seek(0)
            tail = f.read().splitlines()
            if tail:
                last = json.loads(tail[-1].decode("utf-8"))
                prev_hash = last.get("hash")
    envelope = {"ts": int(time.time()*1000), "event": event, "prev": prev_hash}
    canonical = jcs_dumps(envelope)
    h = sha256_hex(canonical)
    record = {"hash": h, **envelope}
    with path.open("ab") as f:
        f.write(json.dumps(record, ensure_ascii=False).encode("utf-8") + b"\n")
        f.flush(); os.fsync(f.fileno())
    return record

# Merkle root over hex leaves
def merkle_root(leaves: Iterable[str]) -> str:
    level = [bytes.fromhex(h) for h in leaves]
    if not level:
        return sha256_hex(b"")
    while len(level) > 1:
        nxt = []
        it = iter(level)
        for a in it:
            try: b = next(it)
            except StopIteration: b = a
            nxt.append(hashlib.sha256(a + b).digest())
        level = nxt
    return level[0].hex()

def file_sha256_hex(path: str) -> str:
    h = hashlib.sha256()
    with open(path, "rb") as f:
        for chunk in iter(lambda: f.read(8192), b""): h.update(chunk)
    return h.hexdigest()

def list_snapshot_file_hashes(root_dir: str) -> List[str]:
    out: List[str] = []
    for dirpath, _, filenames in os.walk(root_dir):
        for name in sorted(filenames):
            p = os.path.join(dirpath, name)
            out.append(file_sha256_hex(p))
    return out

