    (td / "meta.json").write_text(json.dumps(meta, ensure_ascii=False, indent=2), encoding="utf-8")
    append_event_log(str(LOG), {"kind": "task.created", "task_id": task_id, "tie": meta["tie"]})
    return {"task_id": task_id, "status": "created"}

