        "frontend/components/DiffPreviewModal.tsx": open("frontend/components/DiffPreviewModal.tsx","r",encoding="utf-8").read() if pathlib.Path("frontend/components/DiffPreviewModal.tsx").exists() else "// ìƒì„± ì˜ˆì •\n",
