@APP.post("/task/{task_id}/approve")
def approve_task(task_id: str, plan: PlanDTO):
    if not plan.can_approve:
        raise HTTPException(status_code=400, detail={"msg": "TIE ë¯¸ì¶©ì¡±"})
    for f in plan.files:
        p = ROOT / f.path
        p.parent.mkdir(parents=True, exist_ok=True)
        tmp = p.with_suffix(p.suffix + ".tmp")
        tmp.write_text(f.preview, encoding="utf-8")
        os.replace(tmp, p)
    append_event_log(str(LOG), {"kind": "task.approved", "task_id": task_id, "files": [f.path for f in plan.files]})
    return {"ok": True, "written": [f.path for f in plan.files]}

@APP.get("/task/{task_id}/indicator", response_model=IndicatorDTO)
def indicator(task_id: str):
    # last event hash
    last_hash = None
    if LOG.exists():
        with LOG.open("rb") as f:
            try: f.seek(-4096, os.SEEK_END)
            except OSError: f.seek(0)
            tail = f.read().splitlines()
            if tail:
                last = json.loads(tail[-1].decode("utf-8"))
                last_hash = last.get("hash")
    status = "created" if (_task_dir(task_id) / "meta.json").exists() else "unknown"
    return IndicatorDTO(task_id=task_id, status=status, last_event=last_hash, merkle_root=None, can_approve=True)

