"use client";
import React, { useEffect, useState } from "react";

type Indicator = {
  task_id: string;
  status: string;
  last_event?: string | null;
  merkle_root?: string | null;
  can_approve: boolean;
  reasons: string[];
};

export default function TaskIndicatorCard({ taskId }: { taskId: string }) {
  const [ind, setInd] = useState<Indicator | null>(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  async function refresh() {
    setLoading(true);
    setError(null);
    try {
      const res = await fetch(`/task/${taskId}/indicator`);
      if (!res.ok) throw new Error(`HTTP ${res.status}`);
      setInd(await res.json());
    } catch (e: any) {
      setError(e.message);
    } finally {
      setLoading(false);
    }
  }

  async function approve() {
    if (!ind?.can_approve) return;
    setLoading(true);
    try {
      const planRes = await fetch(`/task/${taskId}/plan`, { method: "POST" });
      const plan = await planRes.json();
      const res = await fetch(`/task/${taskId}/approve`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(plan),
      });
      if (!res.ok) throw new Error(`Approve failed: ${res.status}`);
      await refresh();
      alert("ìŠ¹ì¸ ë° ì ìš© ì™„ë£Œ");
    } catch (e: any) {
      setError(e.message);
    } finally {
      setLoading(false);
    }
  }

  useEffect(() => { refresh(); }, [taskId]);

  return (
    <div className="rounded-2xl p-4 shadow">
      <div className="text-xl font-bold">Task Indicator</div>
      {loading && <div>ë¡œë”© ì¤‘â€¦</div>}
      {error && <div className="text-red-600">ì—ëŸ¬: {error}</div>}
      {ind && (
        <div className="space-y-2">
          <div>ID: {ind.task_id}</div>
          <div>Status: {ind.status}</div>
          <div>Last Event: <code>{ind.last_event ?? "â€”"}</code></div>
          <div>Merkle Root: <code>{ind.merkle_root ?? "â€”"}</code></div>
          <button disabled={!ind.can_approve || loading}
            onClick={approve}
            className={"px-3 py-2 rounded " + (ind.can_approve ? "bg-black text-white" : "bg-gray-300 text-gray-600 cursor-not-allowed")}>
            ìŠ¹ì¸í•˜ê³  ì‹¤í–‰
          </button>
        </div>
      )}
    </div>
  );
}

