# backend/app/mcp.py
from __future__ import annotations
import os
import asyncio
from typing import Any, Dict, List, Optional
from dataclasses import dataclass

import httpx

OPENAI_API_KEY = os.getenv("OPENAI_API_KEY", "")
ANTHROPIC_API_KEY = os.getenv("ANTHROPIC_API_KEY", "")
GEMINI_API_KEY = os.getenv("GEMINI_API_KEY", "")

# ê¸°ë³¸ íƒ€ì„ì•„ì›ƒ/ì¬ì‹œë„
TIMEOUT = httpx.Timeout(20.0, connect=10.0)
RETRIES = 2

@dataclass
class LLMResult:
    provider: str
    model: str
    ok: bool
    text: str
    raw: Dict[str, Any]
    error: Optional[str] = None

class LLMProvider:
    name: str

    async def call(self, client: httpx.AsyncClient, prompt: str) -> LLMResult:
        raise NotImplementedError

# ---------- OpenAI (Responses API) ----------
class OpenAIProvider(LLMProvider):
    name = "openai"

    def __init__(self, model_candidates: Optional[List[str]] = None):
        self.model_candidates = model_candidates or ["gpt-5", "gpt-5-large", "gpt-5-mini", "gpt-5-nano"]

    async def _pick_model(self, client: httpx.AsyncClient) -> str:
        r = await client.get(
            "https://api.openai.com/v1/models",
            headers={"Authorization": f"Bearer {OPENAI_API_KEY}"},
            timeout=TIMEOUT,
        )
        r.raise_for_status()
        data = r.json()
        ids = [m["id"] for m in data.get("data", [])]
        for m in self.model_candidates:
            if m in ids:
                return m
        return ids[0] if ids else "gpt-5"

    async def call(self, client: httpx.AsyncClient, prompt: str) -> LLMResult:
        if not OPENAI_API_KEY:
            return LLMResult(self.name, "", False, "", {}, "OPENAI_API_KEY missing")

        # ëª¨ë¸ ì„ íƒ
        try:
            model = await self._pick_model(client)
        except Exception as e:
            return LLMResult(self.name, "", False, "", {}, f"model list error: {e}")

        # temperatureëŠ” í˜„ì¬ ì˜¤ë¥˜ ìœ ë°œ â†’ ì œê±°, reasoning.effortë§Œ ì‚¬ìš©
        payload = {
            "model": model,
            "input": prompt,
            "max_output_tokens": 256,
            "reasoning": {"effort": "low"},
        }

        for attempt in range(RETRIES + 1):
            try:
                r = await client.post(
                    "https://api.openai.com/v1/responses",
                    headers={
                        "Authorization": f"Bearer {OPENAI_API_KEY}",
                        "Content-Type": "application/json",
                    },
                    json=payload,
                    timeout=TIMEOUT,
                )
                if r.status_code >= 400:
                    # ê·¸ëŒ€ë¡œ ë°˜í™˜
                    return LLMResult(self.name, model, False, "", r.json(), f"http {r.status_code}: {r.text}")
                data = r.json()
                # output_text ìš°ì„ , ë°±ì—… ê²½ë¡œ ë³´ì¡°
                text = data.get("output_text") or ""
                if not text:
                    # ì¼ë¶€ ì‘ë‹µ êµ¬ì¡° ë³´ì¡° ì²˜ë¦¬
                    try:
                        out = data.get("output", [])
                        if out and isinstance(out, list):
                            parts = out[0].get("content", [])
                            if parts and isinstance(parts, list) and "text" in parts[0]:
                                text = parts[0]["text"]
                    except Exception:
                        pass
                return LLMResult(self.name, model, True, text.strip(), data)
            except Exception as e:
                if attempt >= RETRIES:
                    return LLMResult(self.name, model, False, "", {}, f"exception: {e}")
                await asyncio.sleep(0.4 * (attempt + 1))

# ---------- Anthropic (Messages API) ----------
class AnthropicProvider(LLMProvider):
    name = "anthropic"

    def __init__(self, preferred: Optional[str] = None):
        self.preferred = preferred  # e.g., "claude-sonnet-4-20250514"

    async def _pick_model(self, client: httpx.AsyncClient) -> str:
        r = await client.get(
            "https://api.anthropic.com/v1/models",
            headers={
                "x-api-key": ANTHROPIC_API_KEY,
                "anthropic-version": "2023-06-01",
            },
            timeout=TIMEOUT,
        )
        r.raise_for_status()
        data = r.json()
        ids = [m["id"] for m in data.get("data", [])]
        if self.preferred and self.preferred in ids:
            return self.preferred
        # sonnet ìš°ì„ 
        for m in ids:
            if "sonnet" in m:
                return m
        return ids[0] if ids else "claude-sonnet-4-20250514"

    async def call(self, client: httpx.AsyncClient, prompt: str) -> LLMResult:
        if not ANTHROPIC_API_KEY:
            return LLMResult(self.name, "", False, "", {}, "ANTHROPIC_API_KEY missing")

        try:
            model = await self._pick_model(client)
        except Exception as e:
            return LLMResult(self.name, "", False, "", {}, f"model list error: {e}")

        payload = {
            "model": model,
            "max_tokens": 512,
            "temperature": 0,
            "messages": [{"role": "user", "content": prompt}],
        }

        for attempt in range(RETRIES + 1):
            try:
                r = await client.post(
                    "https://api.anthropic.com/v1/messages",
                    headers={
                        "x-api-key": ANTHROPIC_API_KEY,
                        "anthropic-version": "2023-06-01",
                        "Content-Type": "application/json",
                    },
                    json=payload,
                    timeout=TIMEOUT,
                )
                if r.status_code >= 400:
                    return LLMResult(self.name, model, False, "", r.json(), f"http {r.status_code}: {r.text}")
                data = r.json()
                text = ""
                try:
                    text = data["content"][0]["text"]
                except Exception:
                    pass
                return LLMResult(self.name, model, True, (text or "").strip(), data)
            except Exception as e:
                if attempt >= RETRIES:
                    return LLMResult(self.name, model, False, "", {}, f"exception: {e}")
                await asyncio.sleep(0.4 * (attempt + 1))

