                  catch { setBadge("err","open/copy failed"); } }
        };
        const btnCopy = document.createElement("button"); btnCopy.textContent = "Copy Path";
        btnCopy.onclick = ()=> copy(meta.path).then(()=>setBadge("ok","copied")).catch(()=>setBadge("err","copy failed"));
        actions.appendChild(btnOpen); actions.appendChild(btnCopy);

        const pathNote = document.createElement("div");
        pathNote.className = "hint mono"; pathNote.textContent = meta.path;

        div.appendChild(h3);
        div.appendChild(metaLine);
        if ((meta.bullets||[]).length){ div.appendChild(ul); }
        div.appendChild(actions);
        div.appendChild(pathNote);
        return div;
      }

      async function refresh(){
