        if (!r.ok) throw new Error("bridge " + r.status);
      }
      function rolesStr(r){ return Array.isArray(r) ? r.join(",") : ""; }

      // Anchors
      const MUST_ANCHORS = [
        "gumgang_meeting/.rules",
        "gumgang_meeting/status/checkpoints/CKPT_72H_RUN.md",
        "gumgang_meeting/app/api.py",
        "gumgang_meeting/status/design/memory_gate/SSOT.md",
      ];
      function renderAnchors(pinned){
        anchorBadges.innerHTML = "";
        const have = new Set(Array.isArray(pinned) ? pinned : []);
        MUST_ANCHORS.forEach((p)=>{
          const span = document.createElement("span");
          const ok = have.has(p);
          span.className = "badge " + (ok ? "ok" : "warn");
          span.textContent = (ok ? "OK " : "MISS ") + p.replace("gumgang_meeting/","");
          anchorBadges.appendChild(span);
        });
      }

      // Top-5
      function pickTop5(nodes){
        const arr = Array.isArray(nodes) ? nodes.slice() : [];
        arr.sort((a,b)=>{
          const sa = (((a||{}).scores||{}).centrality)||0;
          const sb = (((b||{}).scores||{}).centrality)||0;
          return sb - sa;
        });
        return arr.slice(0,5);
      }
      function renderTop5(list){
        topTbody.innerHTML = "";
        list.forEach((n,i)=>{
          const tr = document.createElement("tr");
          const td = (t)=>{ const el=document.createElement("td"); el.textContent=t; return el; };
          tr.appendChild(td(String(i+1)));
          const pathTd = document.createElement("td");
          const code = document.createElement("code");
          code.textContent = n.id || "";
          pathTd.appendChild(code);
          tr.appendChild(pathTd);
          tr.appendChild(td(rolesStr(n.roles || [])));
          tr.appendChild(td(((((n||{}).scores||{}).centrality)||0).toFixed(3)));
          const act = document.createElement("td");
          const btnOpen = document.createElement("button");
          btnOpen.textContent = "Open";
          btnOpen.onclick = async () => {
            try { await bridgeOpenRepoPath(n.id || ""); setBadge("ok","opened"); }
            catch { try { await copy(n.id || ""); setBadge("warn","bridge off â€” copied path"); }
                    catch { setBadge("err","open/copy failed"); } }
          };
          const btnCopy = document.createElement("button");
          btnCopy.textContent = "Copy";
          btnCopy.onclick = () => copy(n.id||"").then(()=>setBadge("ok","copied")).catch(()=>setBadge("err","copy failed"));
          act.appendChild(btnOpen); act.appendChild(btnCopy);
          tr.appendChild(act);
          topTbody.appendChild(tr);
        });
      }

      // Controller
      async function refresh(){
        clearError(); setBadge(null,"loadingâ€¦");
        const now = new Date().toISOString();
        tsEl.textContent = now;
        tsFoot.textContent = now;
        try{
          const lensQ = encodeURIComponent(lensSel.value || "Core");
          const data = await jget(API_BASE + "/api/sitegraph/latest?lens=" + lensQ);
          const snap = (data && data.data) || {};
          const meta = snap.meta || {};
          const nodes = snap.nodes || [];
          const edges = snap.edges || [];
          const anchors = snap.anchors || {};

          snapPathEl.textContent  = (data && data.path) || "â€”";
          snapshotIdEl.textContent= meta.snapshot_id || "â€”";
          createdAtEl.textContent = meta.created_at || "â€”";
          lensValEl.textContent   = meta.lens || "â€”";
          kValEl.textContent      = JSON.stringify(meta.K || {});
          thValEl.textContent     = JSON.stringify(meta.thresholds || {});
          countsEl.textContent    = "nodes:" + nodes.length + ", edges:" + edges.length;

          renderAnchors(anchors.pinned || []);
          renderTop5(pickTop5(nodes));
          setBadge("ok","loaded"); setStatus("ë¡œë“œ ì™„ë£Œ");
        }catch(e){
          showError("ë¡œë“œ ì‹¤íŒ¨: " + (e && e.message ? e.message : e));
          setBadge("warn","load fail"); setStatus("ë¡œë“œ ì‹¤íŒ¨");
        }
      }

      // Wire buttons
      $("btnRefresh").onclick = refresh;
      $("btnRetry").onclick   = refresh;
      $("btnCopyMeta").onclick = ()=>{
        const lines = [
          "API_BASE="   + API_BASE,
          "snapshot_id="+ (snapshotIdEl.textContent||""),
          "created_at=" + (createdAtEl.textContent||""),
          "lens="       + (lensValEl.textContent||""),
          "K="          + (kValEl.textContent||""),
          "thresholds=" + (thValEl.textContent||""),
          "counts="     + (countsEl.textContent||""),
          "path="       + (snapPathEl.textContent||""),
        ];
        copy(lines.join("\n")).then(()=>setBadge("ok","meta copied")).catch(()=>setBadge("err","copy failed"));
      };
      $("btnCopyPath").onclick = ()=>{
        copy(snapPathEl.textContent||"").then(()=>setBadge("ok","path copied")).catch(()=>setBadge("err","copy failed"));
      };
      $("btnOpenJson").onclick = async ()=>{
        const p = snapPathEl.textContent || "";
        if (!p || p==="â€”"){ setBadge("warn","no path"); return; }
        try{ await bridgeOpenRepoPath(p); setBadge("ok","opened"); }
        catch{ try{ await copy(p); setBadge("warn","bridge off â€” copied path"); }
               catch{ setBadge("err","open/copy failed"); } }
      };
      const btnCopyTop = $("btnCopyTop");
      if (btnCopyTop){
        btnCopyTop.onclick = ()=>{
          const rows = Array.from(topTbody.querySelectorAll("tr")).map(tr=>{
            const tds = tr.querySelectorAll("td");
            return [tds[0]?.textContent||"", tds[1]?.textContent||"", tds[2]?.textContent||"", tds[3]?.textContent||""].join("\t");
          }).join("\n");
          copy(rows).then(()=>setBadge("ok","top-5 copied")).catch(()=>setBadge("err","copy failed"));
        };
      }

      // Init
      document.addEventListener("DOMContentLoaded", refresh, { once:true });
    })();
  </script>
</body>
</html>
```

