// 1) REGION ë²”ìœ„ ê³„ì‚°
function regions(model: monaco.editor.ITextModel){
  const text = model.getValue();
  const toPos = (i:number)=> model.getPositionAt(i);
  const ranges = [];
  const regex = /<!--GG_REGION:([A-Z_]+)-->([\s\S]*?)<!--\/GG_REGION:\1-->/g;
  let m; while ((m = regex.exec(text))){
    ranges.push(new monaco.Range(
      toPos(m.index).lineNumber, 1,
      toPos(m.index + m[0].length).lineNumber, 1
    ));
  }
  return ranges;
}

