  async function jget(url, timeoutMs=6000, retries=1){
    for(let a=0;a<=retries;a++){
      const ac = new AbortController();
      const t = setTimeout(()=>ac.abort("timeout"), Math.max(500, timeoutMs*Math.pow(1.5,a)));
      try{
        const r = await fetch(url, {headers:{Accept:"application/json"}, signal: ac.signal, cache:"no-store"});
        clearTimeout(t);
        if(!r.ok) throw new Error(r.status+" "+r.statusText);
        return await r.json();
      }catch(e){
        clearTimeout(t);
        if(a>=retries) throw e;
        await new Promise(res=>setTimeout(res, 400*Math.pow(2,a)));
      }
    }
  }
  function copy(text){
