      snapshotId.textContent = meta.snapshot_id || "â€”";
      createdAt.textContent = meta.created_at || "â€”";
      lensVal.textContent = meta.lens || "â€”";
      counts.textContent = "nodes:" + (nodes.length||0) + ", edges:" + (edges.length||0);

      // Tail/chain best-effort
