const prefRoles = ["C","M","H","A","R","UI","S","D","Q","X","TK","PJ","BZ","HW","NW"];
const clamp01 = x => Math.max(0, Math.min(1, x));
const norm01  = (x, lo, hi) => clamp01(((x ?? 0) - (lo ?? 0)) / Math.max(1e-9, (hi ?? 1) - (lo ?? 0)));

function primaryRole(n){
  const arr = Array.isArray(n.roles) ? n.roles.map(String) : [];
  const single = n.role ? [String(n.role)] : [];
  const roles = arr.length ? arr : single;
  return prefRoles.find(p => roles.includes(p)) || roles[0] || "R";
}

function ringRadiusFor(n, K){
  const base = Number(K?.level0 ?? 8);
  const step = Number(K?.level1 ?? 20);
  const R = lvl => base + step * lvl;
  switch (primaryRole(n)) {
    case "C": return R(0);
    case "M": return R(0.3);
    case "H": return R(1);
    case "A": return R(1.5);
    case "R": return R(2);
    case "UI": return R(2.3);
    case "S": return R(2.6);
    case "D": return R(3);
    case "Q": return R(3.4);
    case "X": return R(3.8);
    case "TK": return R(1.2);
    case "PJ": return R(1.6);
    case "BZ": return R(1.6);
    case "HW": return R(2.2);
    case "NW": return R(2.2);
    default:  return R(2);
  }
}

function recencyOpacity(n, meta){
  const hlDays = Number(meta?.thresholds?.halflife_days ?? 7);
  const hlMs   = Math.max(1, hlDays) * 86400000;
  const mt  = Date.parse(n.mtime || "");
  const ct  = Date.parse(meta?.created_at || "");
  if (!Number.isFinite(mt) || !Number.isFinite(ct)) return 0.85; // ì•ˆì „ ê¸°ë³¸ê°’
  const age = Math.max(0, ct - mt);
  return clamp01(Math.exp(-age / hlMs));
}

