    .nodeVal(n => 2 + 8 * norm01(Number(n.scores?.centrality ?? n.centrality ?? 0), cMin, cMax))
    .nodeOpacity(n => Math.max(0.25, recencyOpacity(n, snap.meta)));

  // ë§í¬(ì—†ìœ¼ë©´ ë”)
  const hasEdges = Array.isArray(snap.edges) && snap.edges.length > 0;
  Graph.linkOpacity(hasEdges ? (options?.centerOn ? 0.18 : 0.0) : 0.0);

