  Graph.d3Force("center", d3.forceCenter(0,0,0));
  Graph.d3Force("charge", d3.forceManyBody().strength(-15));
  Graph.d3Force("collision", d3.forceCollide().radius(n => Math.cbrt(Graph.nodeVal()(n)) * 2 + 1));
  Graph.d3Force("radial", d3.forceRadial(n => ringRadiusFor(n, Kmeta), 0, 0, 0).strength(0.9));
}
