file: ui/overlays/active.css
content: |
  /* === ST-1206 Simple Mode Guardrails â€” append-only override === */
  html, body { height: 100%; }
  body.simple, body.simple html { overflow: hidden !important; } /* A */

  /* min-height shrink chain to allow 1fr contraction */
  body.simple #a1, body.simple #a1-right, body.simple #chat-msgs { min-height: 0; }

  /* A1 wrapper: grid + rows + height + overflow (B) */
  body.simple #a1-wrap {
    display: grid !important;
    grid-template-rows: auto minmax(0,1fr) auto !important;
    height: calc(100dvh - var(--gg-strip-h)) !important;
    overflow: hidden !important;
  }

  /* Scroller whitelist (C) */
  body.simple #gg-threads,
  body.simple #chat-msgs {
    overflow-y: auto !important;
    overscroll-behavior: contain;
  }

  /* Prevent broad overflow reset from touching wrap; keep others visible by default */
  body.simple #a1 *:not(#gg-threads):not(#chat-msgs) { overflow: visible; }

  /* Misplaced heartbeat inside A1 is not allowed (kept at strip) */
  #a1 [data-gg="heartbeat"] { display: none !important; }
[/PATCH]

