file: ui/overlays/active.js
content: |
  // === ST-1206 ensureSimpleGrid & sensor (Simple mode only) ===
  (function(){
    function ensureSimpleGrid(){
      if (!document.body.classList.contains('simple')) return;
      var wrap = document.getElementById('a1-wrap');
      if (!wrap) return;

      // A) global scroll off
      document.documentElement.style.overflow = 'hidden';
      document.body.style.overflow = 'hidden';

      // B) grid rows + height + overflow
      wrap.style.display = 'grid';
      wrap.style.gridTemplateRows = 'auto minmax(0, 1fr) auto';
      wrap.style.height = 'calc(100dvh - var(--gg-strip-h))';
      wrap.style.overflow = 'hidden';

      // C) normalize direct children: keep first 3 rows; move extras into timeline
      var msgs = document.getElementById('chat-msgs');
      if (wrap.children && msgs && wrap.children.length > 3){
        Array.prototype.slice.call(wrap.children, 3).forEach(function(el){ msgs.prepend(el); });
      }

      // D) thinking badge inside timeline as first child (sticky)
      var badge = document.getElementById('gg-thinking');
      if (badge && msgs && !msgs.contains(badge)){
        msgs.insertAdjacentElement('afterbegin', badge);
      }

      // E) composer-actions mark ensure
      var mark = document.querySelector('#a1-wrap [data-gg="composer-actions"]')
             ||  document.querySelector('#a1-wrap #chat-input')?.parentElement;
      if (mark) mark.setAttribute('data-gg', 'composer-actions');
    }

    function assertUIPitstop(){
      try{
        if (localStorage.gg_env === 'prod' || !document.body.classList.contains('simple')) return;
        var wrap = document.getElementById('a1-wrap');
        var cs = wrap && getComputedStyle(wrap);
        var rows = (cs && cs.gridTemplateRows || '').replace(/\s+/g,' ');
        var okRows = /minmax\(0(px)?,\s*1fr\)/.test(rows);

        // scope to #a1 subtree only
        var autos = Array.from(document.querySelectorAll('#a1 *')).filter(function(e){
          var s = getComputedStyle(e);
          return s.overflow === 'auto' || s.overflowY === 'auto' || s.overflowX === 'auto';
        });
        var ids = autos.map(function(e){ return e.id || e.className; });
        var hasThreads = ids.includes('gg-threads');
        var hasMsgs = ids.includes('chat-msgs');
        var extra = autos.filter(function(e){ return !['gg-threads','chat-msgs'].includes(e.id); });

        if (!wrap || cs.display !== 'grid') console.error('WRAP_NOT_GRID', cs && cs.display);
        if (!okRows) console.error('WRAP_ROWS_BAD', rows);
        if (!(hasThreads && hasMsgs) || extra.length) console.error('SCROLLER_VIOLATION', ids);
        if (document.querySelector('#a1 [data-gg="heartbeat"]')) console.error('HEARTBEAT_MISPLACED');
        if (!document.querySelector('[data-gg="composer-actions"]')) console.error('MISSING_COMPOSER_ACTIONS_MARK');
      }catch(e){ try{ console.warn('[ST-1206] sensor warn:', e); }catch(_){ } }
    }

    // idempotent wire-up
    if (document.readyState === 'loading'){
      document.addEventListener('DOMContentLoaded', ensureSimpleGrid, { once:true });
      window.addEventListener('load', ensureSimpleGrid, { once:true });
    } else {
      ensureSimpleGrid();
      window.addEventListener('load', ensureSimpleGrid, { once:true });
    }
    requestAnimationFrame(assertUIPitstop);
  })();
[/PATCH]

