          if (ctrlAlt && (e.key === "m" || e.key === "M")) {
            e.preventDefault();
            showHelp();
            return;
          }
          if (e.ctrlKey && e.altKey && (e.key === "o" || e.key === "O")) {
            e.preventDefault();
            seq = ["O"];
            resetSeqSoon();
            return;
          }
          if (seq.length === 1 && seq[0] === "O") {
            if (e.key === "r" || e.key === "R") {
              e.preventDefault();
              seq.length = 0;
              openEvidence("runtime");
